mapper.select:
SELECT

FROM homework
WHERE course_record_id = #{courseRecordId, jdbcType=INTEGER}

SELECT

FROM homework
WHERE course_record_id = #{courseRecordId, jdbcType=INTEGER}
and user_id = #{userId, jdbcType=INTEGER}

SELECT homework_url
FROM homework
WHERE course_record_id = #{courseRecordId, jdbcType=INTEGER}

SELECT

FROM homework
WHERE
course_id = #{courseId,jdbcType=INTEGER}
AND user_id = #{userId,jdbcType=INTEGER}

mapper.update:
update homework
set homework_url = #{homeworkUrl,jdbcType=VARCHAR},
  homework_name = #{homeworkName,jdbcType=VARCHAR},
  upload_date = #{uploadDate,jdbcType=VARCHAR},
  modify_date = sysdate() + '0',
  score = #{score, jdbcType=VARCHAR},
  comment = #{comment, jdbcType=VARCHAR},
  upload_name = #{uploadName, jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
AND   user_id = #{userId, jdbcType=INTEGER}

UPDATE homework
SET
score = #{score, jdbcType=VARCHAR},
comment = #{comment, jdbcType=VARCHAR}
WHERE
id = #{id, jdbcType=INTEGER}

UPDATE homework
SET
mark_homework_url = #{url}
WHERE
id_number = #{idNumber, jdbcType=VARCHAR}
AND course_record_id = #{courseRecordId, jdbcType=INTEGER}
